const items_layout=document.querySelector("#items-layout");let firstCard,secondCard,hasFlippedCard=!1,lockBoard=!1;function renderItems(items){items.forEach((function(item){items_layout.innerHTML+=`<div class="card-item" data-name="${item.name}">\n            <img class="front-face" src="pictures/VistaRidgeLogo3.png" style="width: 300px; height: 240px;" title="VR Logo" />\n            <img class="back-face" src="${item.image_link}" />\n        </div>`}));const elements=document.getElementsByClassName("card-item");for(let index=0;index<elements.length;index++)elements[index].onclick=function(){flipCard(this)}}function flipCard(item){lockBoard||item!==firstCard&&(item.classList.add("flip"),hasFlippedCard?(secondCard=item,checkForMatch()):(hasFlippedCard=!0,firstCard=item))}function checkForMatch(){firstCard.dataset.name===secondCard.dataset.name?disableMatchedCards():unflipCards()}function disableMatchedCards(){firstCard.onclick=null,secondCard.onclick=null,setTimeout(()=>{alert("match!"),resetBoard()},1e3)}function unflipCards(){lockBoard=!0,setTimeout(()=>{firstCard.classList.remove("flip"),secondCard.classList.remove("flip"),resetBoard()},1500)}function resetBoard(){[hasFlippedCard,lockBoard]=[!1,!1],[firstCard,secondCard]=[null,null]}function readJSONFile(file){fetch(file).then(response=>response.json()).then(data=>{totalItems=data.items,renderItems(totalItems)})}readJSONFile("seeders/data.json");